std::vector<char> to_vector(string src){
   std::vector<char> r;
   for(int i=0;i<src.length();i+=2){
      char first = src[i] - '0';
      char second = src[i+1] - '0';
      char result = (first << 4) + second;
      r.push_back(result);
   }
   return r;
}


bool vector_eq(std::vector<char> v1,std::vector<char> v2){
   if(v1.size()!=v2.size())
      return false;

   for(int i=0;i<v1.size();i++){
      if(v1[i]!=v2[i])
         return false;
   }

   return true;

}






